import React, { useState, useEffect } from 'react'
import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import SetBirthyear from './components/SetBirthyear'
import ApolloClient from 'apollo-boost'
import { gql } from 'apollo-boost'
import { Query, useMutation, Mutation, useApolloClient } from 'react-apollo'
import LoginForm from './components/LoginForm'




const NEW_BOOK = gql`
  mutation newBook($title: String!, $author: String!, $published: Int!, $genres: [String!]){
     addBook(
      title: $title,
      author: $author,
      published: $published,
      genres: $genres
      ){
        title
        published
        genres
      }
}`


const NEW_BIRTHYEAR = gql `

  mutation setBirthyear($name: String!, $birthyear: Int!){
      editAuthor(
        name: $name,
        setBornTo: $birthyear
      ){
        name
        born
      }
  }`

const ALL_AUTHORS = gql`
{
  allAuthors {
    name,
    born,
    id
  }
}
`

const ALL_BOOKS = gql`
{
  allBooks {
    title,
    published,
  }
}
`

const LOGIN = gql`
  mutation login($username: String!, $password: String!) {
    login(username: $username, password: $password)  {
      value
    }
  }
`

const App = () => {
  const [page, setPage] = useState('authors')
  const [token, setToken] = useState(null)
  const [errorMessage, setErrorMessage] = useState(null)
  const [login] = useMutation(LOGIN, {
    onError: handleError
  })

  const client = useApolloClient()


  const handleError = (error) => {
    setErrorMessage(error.graphQLErrors[0].message)
    setTimeout(() => {
      setErrorMessage(null)
    }, 10000)
  }

  /*const useEffect = () => {
    localStorage.removeItem('whatsoever-user-token')
  }*/

  const errorNotification = () => errorMessage &&
    <div style={{ color: 'red' }}>
      {errorMessage}
    </div>

  const logout = () => {
    setToken(null)
    localStorage.clear()
    client.resetStore()
  }

   if(token === null){
    return (
      <div>
        <h2>Login</h2>
        <LoginForm
          login={login}
          setToken={(token) => setToken(token)}
        />
      </div>
    )
   }else{
    return (
      <div>
        <div>
          <button onClick={() => setPage('authors')}>authors</button>
          <button onClick={() => setPage('books')}>books</button>
          <button onClick={() => setPage('add')}>add book</button>
          <button onClick={() => setPage('set')}>set birthyear</button>
          <button onClick={logout}>logout</button>
        </div>

        <Query query={ALL_AUTHORS}>
          {(result) => 
            <Authors
              show={page === 'authors'} result={result} 
            />}
        </Query>

        <Query query={ALL_BOOKS}>
          {(result) => 
            <Books
              show={page === 'books'} result={result}
            />}
        </Query>
        <Mutation 
          mutation={NEW_BOOK}
          refetchQueries={[{query: ALL_BOOKS},{query: ALL_AUTHORS}]}>
          {(addBook) => 
            <NewBook show={page ==='add'} addBook={addBook} />
          }
        </Mutation>
        <Mutation 
          mutation={NEW_BIRTHYEAR}
          refetchQueries={[{query: ALL_BOOKS},{query: ALL_AUTHORS}]}>
          {(setYear) => 
             <SetBirthyear show={page ==='set'} setYear={setYear}/> }      
        </Mutation>
      </div>
    )
   }

}

export default App
