import React from 'react'

const Books = (props) => {
  if (!props.show) {
    return null
  }

  if(props.result.loading){
    return <div>loading...</div>
  }
  console.log(props)
  const books = props.result.data.allBooks
  if(books === null){
    return null
  }else{
    return (
      <div>
        <h2>books</h2>

        <table>
          <tbody>
            <tr>
              <th></th>
              <th>
                author
              </th>
              <th>
                published
              </th>
              <th>
                genres
              </th>
            </tr>
            {books.map(a =>
              <tr key={a.title}>
                <td>{a.title}</td>
                <td>{a.id}</td>
                <td>{a.published}</td>
                <td>{a.genres}</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    )
  }
}

export default Books
