import React, { useState } from 'react'
import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import SetBirthyear from './components/SetBirthyear'
import { gql } from 'apollo-boost'
import { Query, Mutation } from 'react-apollo'

const NEW_BOOK = gql`
  mutation newBook($title: String!, $author: String!, $published: Int!, $genres: [String!]){
     addBook(
      title: $title,
      author: $author,
      published: $published,
      genres: $genres
      ){
        title
        author
        published
        genres
      }
}`


const NEW_BIRTHYEAR = gql `

  mutation setBirthyear($name: String!, $birthyear: Int!){
      editAuthor(
        name: $name,
        setBornTo: $birthyear
      ){
        name
        born
      }
  }`

const ALL_AUTHORS = gql`
{
  allAuthors {
    name,
    born,
    bookCount,
    id
  }
}
`

const ALL_BOOKS = gql`
{
  allBooks {
    title,
    author,
    published,
  }
}
`

const App = () => {
  const [page, setPage] = useState('authors')
  const [authors, setAuthors] = useState([])

   return (
    <div>
      <div>
        <button onClick={() => setPage('authors')}>authors</button>
        <button onClick={() => setPage('books')}>books</button>
        <button onClick={() => setPage('add')}>add book</button>
        <button onClick={() => setPage('set')}>set birthyear</button>
      </div>

      <Query query={ALL_AUTHORS}>
        {(result) => 
          <Authors
            show={page === 'authors'} result={result} 
          />}
      </Query>

      <Query query={ALL_BOOKS}>
        {(result) => 
          <Books
            show={page === 'books'} result={result}
          />}
      </Query>
      <Mutation 
        mutation={NEW_BOOK}
        refetchQueries={[{query: ALL_BOOKS},{query: ALL_AUTHORS}]}>
        {(addBook) => 
          <NewBook show={page ==='add'} addBook={addBook} />
        }
      </Mutation>
      <Mutation 
        mutation={NEW_BIRTHYEAR}
        refetchQueries={[{query: ALL_BOOKS},{query: ALL_AUTHORS}]}>
        {(setYear) => 
           <SetBirthyear show={page ==='set'} setYear={setYear} authors={authors}/> }      
      </Mutation>
    </div>
  )
}

export default App
