import React from 'react'

const Authors = (props) => {

  if (!props.show) {
    return null
  }

  if(props.result.data === undefined){
    return null
  }

  if(props.result.loading){
    return <div>loading....</div>
  }
  console.log("All props:", props)
  const authors = (props.result.data.allAuthors)

  return (
    <div>
      <h2>authors</h2>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              born
            </th>
            <th>
              books
            </th>
          </tr>
          {authors.map(a =>
            <tr key={a.name}>
              <td>{a.name}</td>
              <td>{a.born}</td>
              <td>{a.bookCount}</td>
            </tr>
          )}
        </tbody>
      </table>

    </div>
  )
}

export default Authors
