import React, { useState } from 'react'

const Books = (props) => {
  const [filter, setFilter] = useState('RESET')


  if (!props.show) {
    return null
  }

  if(props.result.loading){
    return <div>loading...</div>
  }
  const books = props.result.data.allBooks
  let filteredbooks = null
  if(filter !== 'RESET'){
    filteredbooks = books.filter(book => book.genres === filter)
  }else{
    filteredbooks = books
  }


  const set = new Set()
  let genreList = books.map(book => {
    const list = []
    for (let x = 0; x < book.genres.length; x++){

      let value = set.has(book.genres[x])
      console.log(book.genres[x])
      if(!value){
        set.add(book.genres[x])
        list.concat(book.genres[x])
      }
    }
    return list
  })
  genreList.push('RESET')




  const handleClick = (e) => {
    e.preventDefault()
    console.log(filter)
  }

  if(books === null){
    return null
  }else{
    return (
      <div>
        <h2>books</h2>

        <table>
          <tbody>
            <tr>
              <th></th>
              <th>
                author
              </th>
              <th>
                published
              </th>
              <th>
                genres
              </th>
            </tr>
            {filteredbooks.map(a =>
              <tr key={a.title}>
                <td>{a.title}</td>
                <td>{a.author.name}</td>
                <td>{a.published}</td>
                <td>{a.genres}</td>
              </tr>
            )}
          </tbody>
        </table>
        <form onSubmit={handleClick}>
          {genreList.map((genre, x) => 
            <button key={x} onClick={() => setFilter(genre)} type='submit'>{genre}</button>)
          }
        </form>
      </div>
    )
  }
}

export default Books
