import React, { useState } from 'react'

const Books = (props) => {
  const [filter, setFilter] = useState('')


  if (!props.show) {
    return null
  }

  if(props.result.loading){
    return <div>loading...</div>
  }
  const books = props.result.data.allBooks
  let filteredbooks = null
  if(filter !== ''){
    filteredbooks = books.filter(book => book.genres === filter)
  }else{
    filteredbooks = books
  }
  const set = new Set()
  const genreList = books.map(book => {
    const value = set.has(book.genres)
    if(value){
      return false
    }
    set.add(book.genres)
    return true
  })


  const handleClick = (e) => {
    e.preventDefault()
    console.log(filter)
  }

  if(books === null){
    return null
  }else{
    return (
      <div>
        <h2>books</h2>

        <table>
          <tbody>
            <tr>
              <th></th>
              <th>
                author
              </th>
              <th>
                published
              </th>
              <th>
                genres
              </th>
            </tr>
            {filteredbooks.map(a =>
              <tr key={a.title}>
                <td>{a.title}</td>
                <td>{a.author.name}</td>
                <td>{a.published}</td>
                <td>{a.genres}</td>
              </tr>
            )}
          </tbody>
        </table>
        <form onSubmit={handleClick}>
          {genreList.map(genre => 
            <button key={genre.title} onClick={() => setFilter(genre)} type='submit'>{genre}</button>)
          }
        </form>
      </div>
    )
  }
}

export default Books
