import React from 'react'

const Books = (props) => {
  if (!props.show) {
    return null
  }

  if(props.result.loading){
    return <div>loading...</div>
  }
  const books = props.result.data.allBooks


  const handleClick = () => {
    console.log('click')
  }

  if(books === null){
    return null
  }else{
    return (
      <div>
        <h2>books</h2>

        <table>
          <tbody>
            <tr>
              <th></th>
              <th>
                author
              </th>
              <th>
                published
              </th>
              <th>
                genres
              </th>
            </tr>
            {books.map(a =>
              <tr key={a.title}>
                <td>{a.title}</td>
                <td>{a.author.name}</td>
                <td>{a.published}</td>
                <td>{a.genres}</td>
              </tr>
            )}
          </tbody>
        </table>
        {books.map(book => 
          <button onClick={handleClick} type='submit'>{book.genres}</button>)
        }
      </div>
    )
  }
}

export default Books
