import React, { useState } from 'react'
import Select from 'react-select'
import { gql } from 'apollo-boost'
import { Query, Mutation } from 'react-apollo'

const ALL_AUTHORS = gql`
{
  allAuthors {
    name,
    born,
    bookCount,
    id
  }
}
`


const SetBirthyear = (props) => {


    const [name, setName] = useState('')
    const [birthyear, setBirthyear] = useState('')

    if (!props.show) {
        return null
    }

    const options = props.authors.map(author => ({value: author.title, label: author.title}))


    const handleChange = (name) => {
       console.log("option selected: ", name)
       setName(name)

    }

    const handleSetBirthyear = async (event) => {
        event.preventDefault()
        console.log('setting birthyear...')
        await props.setYear({
            variables: {name, birthyear}
        })

        setName('')
        setBirthyear('')

    }



    return (
        <div>
            <br></br>
            <h2>set birthyear</h2>
            <form onSubmit={handleSetBirthyear}>
            <div>
            <Select
                value={name}
                onChange={handleChange}
                options={options}
            />
            </div>
            <div>
                Birthyear: <input 
                                value={birthyear}
                                type='number'
                                onChange={( {target} ) => setBirthyear(Number(target.value))}
                            />
            </div>
            <button type='submit'>set birthyear</button>
            </form>
        </div>
    )



}


export default SetBirthyear
